select.sql:
select p.*, round(p.Price/10) Salary, gt.TypeID as Type, gt.Name_$lang; as TypeName,c.Name_$lang; as Country, concat(p.Name,' [',p.Level,']') as Gladiator, gt.TypeID as TypeID
from ut_tavern p left outer join ut_countries c on c.CountryID=p.CountryID left outer join ut_gladiator_types gt using(TypeID)
where (p.UserID='$auth->user;') and '$auth->user;'<>'' order by p.Name asc
